mapper.select:
select * from app_user_bonus where 1=1
id = #{id,jdbcType=BIGINT} and user_id = #{userId,jdbcType=BIGINT} and event_msg = #{eventMsg,jdbcType=VARCHAR} and bonus = #{bonus,jdbcType=INTEGER} and
limit #{start},#{pageSize}

mapper.update:
update app_user_bonus
user_id = #{userId,jdbcType=BIGINT}, event_msg = #{eventMsg,jdbcType=VARCHAR}, bonus = #{bonus,jdbcType=INTEGER}, create = #{create,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=BIGINT}
