mapper.select:
select * from app_user where 1=1
union_id = #{unionId,jdbcType=VARCHAR} and mobile_number = #{mobileNumber,jdbcType=VARCHAR} and nick_name = #{nickName,jdbcType=VARCHAR} and password = #{password,jdbcType=VARCHAR} and level = #{level,jdbcType=INTEGER} and
limit #{start},#{pageSize}

mapper.update:
update app_user
union_id = #{unionId,jdbcType=VARCHAR}, mobile_number = #{mobileNumber,jdbcType=VARCHAR}, nick_name = #{nickName,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, level = #{level,jdbcType=INTEGER},
where id = #{id,jdbcType=BIGINT}
